<?php namespace Illuminate\Auth\Console; use Illuminate\Console\Command; use Illuminate\Filesystem\Filesystem; class RemindersControllerCommand extends Command { protected $name = 'auth:reminders-controller'; protected $description = 'Create a stub password reminder controller'; protected $files; public function __construct(Filesystem $files) { parent::__construct(); $this->files = $files; } public function fire() { $destination = $this->laravel['path'].'/controllers/RemindersController.php'; if ( ! $this->files->exists($destination)) { $this->files->copy(__DIR__.'/stubs/controller.stub', $destination); $this->info('Password reminders controller created successfully!'); $this->comment("Route: Route::controller('password', 'RemindersController');"); } else { $this->error('Password reminders controller already exists!'); } } }
