<?php namespace Illuminate\Auth; class GenericUser implements UserInterface { protected $attributes; public function __construct(array $attributes) { $this->attributes = $attributes; } public function getAuthIdentifier() { return $this->attributes['id']; } public function getAuthPassword() { return $this->attributes['password']; } public function __get($key) { return $this->attributes[$key]; } public function __set($key, $value) { $this->attributes[$key] = $value; } public function __isset($key) { return isset($this->attributes[$key]); } public function __unset($key) { unset($this->attributes[$key]); } }
