<?php namespace Illuminate\Cache; class ApcStore extends TaggableStore implements StoreInterface { protected $apc; protected $prefix; public function __construct(ApcWrapper $apc, $prefix = '') { $this->apc = $apc; $this->prefix = $prefix; } public function get($key) { $value = $this->apc->get($this->prefix.$key); if ($value !== false) { return $value; } } public function put($key, $value, $minutes) { $this->apc->put($this->prefix.$key, $value, $minutes build composer.json composer.lock CONTRIBUTING.md LICENSE.txt phpmin.sh phpunit.php phpunit.xml readme.md src tests 60); } public function increment($key, $value = 1) { return $this->apc->increment($this->prefix.$key, $value); } public function decrement($key, $value = 1) { return $this->apc->decrement($this->prefix.$key, $value); } public function forever($key, $value) { return $this->put($key, $value, 0); } public function forget($key) { $this->apc->delete($this->prefix.$key); } public function flush() { $this->apc->flush(); } public function getPrefix() { return $this->prefix; } }
