<?php namespace Illuminate\Cache\Console; use Illuminate\Console\Command; use Illuminate\Cache\CacheManager; use Illuminate\Filesystem\Filesystem; class ClearCommand extends Command { protected $name = 'cache:clear'; protected $description = "Flush the application cache"; protected $cache; protected $files; public function __construct(CacheManager $cache, Filesystem $files) { parent::__construct(); $this->cache = $cache; $this->files = $files; } public function fire() { $this->cache->flush(); $this->files->delete($this->laravel['config']['app.manifest'].'/services.json'); $this->info('Application cache cleared!'); } }
