<?php namespace Illuminate\Config; class EnvironmentVariables { protected $loader; public function __construct(EnvironmentVariablesLoaderInterface $loader) { $this->loader = $loader; } public function load($environment = null) { foreach ($this->loader->load($environment) as $key => $value) { $_ENV[$key] = $value; $_SERVER[$key] = $value; putenv("{$key}={$value}"); } } }
