<?php namespace Illuminate\Config; use Illuminate\Filesystem\Filesystem; class FileEnvironmentVariablesLoader implements EnvironmentVariablesLoaderInterface { protected $files; protected $path; public function __construct(Filesystem $files, $path = null) { $this->files = $files; $this->path = $path ?: base_path(); } public function load($environment = null) { if ($environment == 'production') $environment = null; if ( ! $this->files->exists($path = $this->getFile($environment))) { return array(); } else { return $this->files->getRequire($path); } } protected function getFile($environment) { if ($environment) { return $this->path.'/.env.'.$environment.'.php'; } else { return $this->path.'/.env.php'; } } }
