<?php namespace Illuminate\Database\Connectors; class MySqlConnector extends Connector implements ConnectorInterface { public function connect(array $config) { $dsn = $this->getDsn($config); $options = $this->getOptions($config); $connection = $this->createConnection($dsn, $config, $options); $collation = $config['collation']; $charset = $config['charset']; $names = "set names '$charset'". ( ! is_null($collation) ? " collate '$collation'" : ''); $connection->prepare($names)->execute(); if (isset($config['strict']) && $config['strict']) { $connection->prepare("set session sql_mode='STRICT_ALL_TABLES'")->execute(); } return $connection; } protected function getDsn(array $config) { extract($config); $dsn = "mysql:host={$host};dbname={$database}"; if (isset($config['port'])) { $dsn .= ";port={$port}"; } if (isset($config['unix_socket'])) { $dsn .= ";unix_socket={$config['unix_socket']}"; } return $dsn; } }
