<?php namespace Illuminate\Database\Connectors; use PDO; class SqlServerConnector extends Connector implements ConnectorInterface { protected $options = array( PDO::ATTR_CASE => PDO::CASE_NATURAL, PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION, PDO::ATTR_ORACLE_NULLS => PDO::NULL_NATURAL, PDO::ATTR_STRINGIFY_FETCHES => false, ); public function connect(array $config) { $options = $this->getOptions($config); return $this->createConnection($this->getDsn($config), $config, $options); } protected function getDsn(array $config) { extract($config); $port = isset($config['port']) ? ','.$port : ''; if (in_array('dblib', $this->getAvailableDrivers())) { return "dblib:host={$host}{$port};dbname={$database}"; } else { $dbName = $database != '' ? ";Database={$database}" : ''; return "sqlsrv:Server={$host}{$port}{$dbName}"; } } protected function getAvailableDrivers() { return PDO::getAvailableDrivers(); } }
