<?php namespace Illuminate\Database\Console\Migrations; use Illuminate\Console\Command; class BaseCommand extends Command { protected function getMigrationPath() { $path = $this->input->getOption('path'); if ( ! is_null($path)) { return $this->laravel['path.base'].'/'.$path; } $package = $this->input->getOption('package'); if ( ! is_null($package)) { return $this->packagePath.'/'.$package.'/src/migrations'; } $bench = $this->input->getOption('bench'); if ( ! is_null($bench)) { $path = "/workbench/{$bench}/src/migrations"; return $this->laravel['path.base'].$path; } return $this->laravel['path'].'/database/migrations'; } }
