<?php namespace Illuminate\Database\Eloquent\Relations; use Illuminate\Database\Eloquent\Builder; class MorphPivot extends Pivot { protected function setKeysForSaveQuery(Builder $query) { $query->where($this->morphType, $this->getAttribute($this->morphType)); return parent::setKeysForSaveQuery($query); } public function delete() { $query = $this->getDeleteQuery(); $query->where($this->morphType, $this->getAttribute($this->morphType)); return $query->delete(); } public function setMorphType($morphType) { $this->morphType = $morphType; return $this; } }
