<?php namespace Illuminate\Foundation\Console; use Illuminate\Console\Command; use Illuminate\Foundation\Composer; use Symfony\Component\Finder\Finder; class AutoloadCommand extends Command { protected $name = 'dump-autoload'; protected $description = "Regenerate framework autoload files"; protected $composer; public function __construct(Composer $composer) { parent::__construct(); $this->composer = $composer; } public function fire() { $this->call('optimize'); foreach ($this->findWorkbenches() as $workbench) { $this->comment("Running for workbench [{$workbench['name']}]..."); $this->composer->setWorkingPath($workbench['path'])->dumpOptimized(); } } protected function findWorkbenches() { $results = array(); foreach ($this->getWorkbenchComposers() as $file) { $results[] = array('name' => $file->getRelativePath(), 'path' => $file->getPath()); } return $results; } protected function getWorkbenchComposers() { $workbench = $this->laravel['path.base'].'/workbench'; if ( ! is_dir($workbench)) return array(); return Finder::create()->files()->in($workbench)->name('composer.json')->depth('< 3'); } }
