<?php namespace Illuminate\Foundation\Console; use Illuminate\Console\Command; class ClearCompiledCommand extends Command { protected $name = 'clear-compiled'; protected $description = "Remove the compiled class file"; public function fire() { if (file_exists($path = $this->laravel['path.base'].'/bootstrap/compiled.php')) { @unlink($path); } if (file_exists($path = $this->laravel['config']['app.manifest'].'/services.json')) { @unlink($path); } } }
