<?php namespace Illuminate\Foundation\Providers; use Illuminate\Support\ServiceProvider; use Illuminate\Foundation\Console\CommandMakeCommand; class CommandCreatorServiceProvider extends ServiceProvider { protected $defer = true; public function register() { $this->app->bindShared('command.command.make', function($app) { return new CommandMakeCommand($app['files']); }); $this->commands('command.command.make'); } public function provides() { return array( 'command.command.make', ); } }
