<?php namespace Illuminate\Foundation\Providers; use Illuminate\Support\ServiceProvider; use Illuminate\Foundation\Console\KeyGenerateCommand; class KeyGeneratorServiceProvider extends ServiceProvider { protected $defer = true; public function register() { $this->app->bindShared('command.key.generate', function($app) { return new KeyGenerateCommand($app['files']); }); $this->commands('command.key.generate'); } public function provides() { return array('command.key.generate'); } }
