<?php namespace Illuminate\Foundation\Providers; use Illuminate\Support\ServiceProvider; use Illuminate\Foundation\Console\RoutesCommand; class RouteListServiceProvider extends ServiceProvider { protected $defer = true; public function register() { $this->app->bindShared('command.routes', function($app) { return new RoutesCommand($app['router']); }); $this->commands('command.routes'); } public function provides() { return array('command.routes'); } }
