<?php namespace Illuminate\Log; use Monolog\Logger; use Illuminate\Support\ServiceProvider; class LogServiceProvider extends ServiceProvider { protected $defer = true; public function register() { $logger = new Writer( new Logger($this->app['env']), $this->app['events'] ); $this->app->instance('log', $logger); if (isset($this->app['log.setup'])) { call_user_func($this->app['log.setup'], $logger); } } public function provides() { return array('log'); } }
