<?php namespace Illuminate\Pagination; use Illuminate\Support\ServiceProvider; class PaginationServiceProvider extends ServiceProvider { protected $defer = true; public function register() { $this->app->bindShared('paginator', function($app) { $paginator = new Factory($app['request'], $app['view'], $app['translator']); $paginator->setViewName($app['config']['view.pagination']); $app->refresh('request', $paginator, 'setRequest'); return $paginator; }); } public function provides() { return array('paginator'); } }
