<?php namespace Illuminate\Queue\Connectors; use IronMQ; use Illuminate\Http\Request; use Illuminate\Queue\IronQueue; use Illuminate\Encryption\Encrypter; class IronConnector implements ConnectorInterface { protected $crypt; protected $request; public function __construct(Encrypter $crypt, Request $request) { $this->crypt = $crypt; $this->request = $request; } public function connect(array $config) { $ironConfig = array('token' => $config['token'], 'project_id' => $config['project']); if (isset($config['host'])) $ironConfig['host'] = $config['host']; return new IronQueue(new IronMQ($ironConfig), $this->crypt, $this->request, $config['queue'], $config['encrypt']); } }
