<?php namespace Illuminate\Queue\Connectors; use Illuminate\Redis\Database; use Illuminate\Queue\RedisQueue; class RedisConnector implements ConnectorInterface { protected $redis; protected $connection; public function __construct(Database $redis, $connection = null) { $this->redis = $redis; $this->connection = $connection; } public function connect(array $config) { return new RedisQueue($this->redis, $config['queue'], $this->connection); } }
