<?php namespace Illuminate\Queue\Connectors; use Aws\Sqs\SqsClient; use Illuminate\Queue\SqsQueue; class SqsConnector implements ConnectorInterface { public function connect(array $config) { $sqsConfig = array_only($config, array('key', 'secret', 'region', 'default_cache_config')); $sqs = SqsClient::factory($sqsConfig); return new SqsQueue($sqs, $config['queue']); } }
