<?php namespace Illuminate\Queue; class SyncQueue extends Queue implements QueueInterface { public function push($job, $data = '', $queue = null) { $this->resolveJob($job, json_encode($data))->fire(); return 0; } public function pushRaw($payload, $queue = null, array $options = array()) { } public function later($delay, $job, $data = '', $queue = null) { return $this->push($job, $data, $queue); } public function pop($queue = null) {} protected function resolveJob($job, $data) { return new Jobs\SyncJob($this->container, $job, $data); } }
