<?php namespace Illuminate\Session; use Illuminate\Support\ServiceProvider; class CommandsServiceProvider extends ServiceProvider { protected $defer = true; public function register() { $this->app->bindShared('command.session.database', function($app) { return new Console\SessionTableCommand($app['files']); }); $this->commands('command.session.database'); } public function provides() { return array('command.session.database'); } }
