<?php namespace Illuminate\Support; use Countable; class ViewErrorBag implements Countable { protected $bags = []; public function getBag($key) { return array_get($this->bags, $key, new MessageBag); } public function put($key, MessageBag $bag) { $this->bags[$key] = $bag; return $this; } public function count() { return $this->default->count(); } public function __call($method, $parameters) { return call_user_func_array(array($this->default, $method), $parameters); } public function __get($key) { return array_get($this->bags, $key, new MessageBag); } public function __set($key, $value) { array_set($this->bags, $key, $value); } }
