<?php namespace Illuminate\View\Engines; use Closure; class EngineResolver { protected $resolvers = array(); protected $resolved = array(); public function register($engine, Closure $resolver) { $this->resolvers[$engine] = $resolver; } public function resolve($engine) { if ( ! isset($this->resolved[$engine])) { $this->resolved[$engine] = call_user_func($this->resolvers[$engine]); } return $this->resolved[$engine]; } }
