<?php namespace Illuminate\View\Engines; use Illuminate\View\Exception; class PhpEngine implements EngineInterface { public function get($path, array $data = array()) { return $this->evaluatePath($path, $data); } protected function evaluatePath($__path, $__data) { ob_start(); extract($__data); try { include $__path; } catch (\Exception $e) { $this->handleViewException($e); } return ltrim(ob_get_clean()); } protected function handleViewException($e) { ob_get_clean(); throw $e; } }
