<?php namespace Illuminate\Workbench; class Package { public $vendor; public $lowerVendor; public $name; public $lowerName; public $author; public $email; public function __construct($vendor, $name, $author, $email) { $this->name = $name; $this->email = $email; $this->vendor = $vendor; $this->author = $author; $this->lowerName = snake_case($name, '-'); $this->lowerVendor = snake_case($vendor, '-'); } public function getFullName() { return $this->lowerVendor.'/'.$this->lowerName; } }
