<?php namespace Illuminate\Workbench; use Illuminate\Support\ServiceProvider; use Illuminate\Workbench\Console\WorkbenchMakeCommand; class WorkbenchServiceProvider extends ServiceProvider { protected $defer = false; public function register() { $this->app->bindShared('package.creator', function($app) { return new PackageCreator($app['files']); }); $this->app->bindShared('command.workbench', function($app) { return new WorkbenchMakeCommand($app['package.creator']); }); $this->commands('command.workbench'); } public function provides() { return array('package.creator', 'command.workbench'); } }
